import * as React from "react";
import { useState } from "react";
import "./App.css";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import Box from "@mui/material/Box";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";

function Signup() {
  const [name, setName] = useState("");
  const [service, setService] = useState("");
  const [mobile, setMobile] = useState();
  const [password, setPassword] = useState("");
  const [rate, setRate] = useState(0);

  const handleClick = () => {
    let n, m, p, r;

    n = name;
    m = mobile;
    p = password;
    let Plumber = new Array([]);
    let Electrician = new Array([]);
    let Carpenters = new Array([]);

    if (service === "Plumber") {
      Plumber = JSON.parse(localStorage.getItem("Plumber"))
        ? JSON.parse(localStorage.getItem("Plumber"))
        : [];

      Plumber.push({
        name: n,
        mobile: m,
        password: p,
      });
      localStorage.setItem("Plumber", JSON.stringify(Plumber));
    }

    if (service === "Electrician") {
      Electrician = JSON.parse(localStorage.getItem("Electrician"))
        ? JSON.parse(localStorage.getItem("Electrician"))
        : [];

      Electrician.push({
        name: n,
        mobile: m,
        password: p,
      });
      localStorage.setItem("Electrician", JSON.stringify(Electrician));
    }

    if (service === "Carpenters") {
      Carpenters = JSON.parse(localStorage.getItem("Carpenters"))
        ? JSON.parse(localStorage.getItem("Carpenters"))
        : [];

      Carpenters.push({
        name: n,
        mobile: m,
        password: p,
      });
      localStorage.setItem("Carpenters", JSON.stringify(Carpenters));
    }
  };

  return (
    <>
      <form id="form" action="">
        <TextField
          id="outlined-required"
          label="Name"
          placeholder="Enter your name here"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
        <br /> <br />
        <TextField
          id="outlined-number"
          autoComplete="username"
          label="Mobile Number"
          type="number"
          value={mobile}
          onChange={(event) => setMobile(event.target.value)}
        />{" "}
        <br /> <br />
        <Box sx={{ maxWidth: 235, marginLeft: 43 }}>
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">Services</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={service}
              label="Services"
              onChange={(event) => setService(event.target.value)}
              required
              autoComplete="services"
            >
              <MenuItem value={"Plumber"}>Plumber</MenuItem>
              <MenuItem value={"Electrician"}>Electrician</MenuItem>
              <MenuItem value={"Carpenters"}>Carpenters</MenuItem>
            </Select>
          </FormControl>
        </Box>{" "}
        <br />
        <TextField
          id="outlined-password-input"
          label="Password"
          type="password"
          autoComplete="current-password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />
        <br /> <br />
        <TextField
          id="outlined-number"
          autoComplete="rate"
          label="Price"
          type="number"
          value={rate}
          onChange={(event) => setRate(event.target.value)}
        />{" "}
        <br /> <br />
        <Button onClick={handleClick} variant="outlined">
          Sign Up
        </Button>
      </form>
    </>
  );
}

export default Signup;
