import { createSlice } from "@reduxjs/toolkit";

export const dataReducer = createSlice({
  name: "Service Data",
  initialState: {
    value: 0,
  },
  reducers: {
    serviceData: (state) => {
      state.value += 1;
    },
  },
});

export const { serviceData } = dataReducer.actions;

export default dataReducer.reducer;
