export function postData(data) {
  fetch("http://localhost:3001/users", {
    method: "POST",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  }).then((res) => res.json());
}

export function fetchSingleUserData() {
  fetch("http://localhost:3001/users")
    .then((res) => res.json())
    .then((res) => {
      //   console.log(res.users[res.users.length - 1]);
      return res.users[res.users.length - 1];
    });
}
export function deleteUserData(id) {
  fetch(`http://localhost:3001/users/${id}`, {
    method: "DELETE",
  });
}

export function updateData(id) {
  fetch(`http://localhost:3001/users/${id}`, {
    method: "PUT",
    headers: {
      Accept: "application/json",
      "Content-Type": "application/json",
    },
    body: JSON.stringify(data),
  }).then((res) => res.json());
}

export default (postData, fetchSingleUserData, deleteUserData);
