import * as React from "react";
import { useState, useEffect } from "react";
import "./App.css";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { withRouter } from "react-router";
import { checkUserOrServiceProvider, login } from "./apiServices";

function Login(props) {
  const [user, setUser] = useState({
    email: "",
    password: "",
  });

  useEffect(() => {
    localStorage.setItem("token", undefined);
  }, []);

  const handleClick = () => {
    let data = {
      email: user.email,
      password: user.password,
    };

    if (user.email !== "" && user.password !== "") {
      var promise = new Promise((resolve) => {
        login(data);
        setTimeout(() => {
          resolve();
        }, 1000);
      });

      var promise2 = new Promise((resolve) => {
        const loginData = setTimeout(() => {
          checkUserOrServiceProvider(user.email);
        }, 2000);

        setTimeout(() => {
          resolve(loginData);
        }, 1000);
      });

      promise.then(() => {
        console.log("hii");
        if (localStorage.getItem("token") !== "undefined") {
          promise2.then((loginData) => {
            console.log(loginData);
            if (loginData.role !== null)
              props.history.push({
                pathname: "/userDetails",
                state: { email: user.email },
              });
            else {
              props.history.push("/dashboard");
            }
          });
        } else {
          alert("incorrect email or password");
        }
      });
    } else {
      alert("Fill all details properly");
    }
  };

  return (
    <>
      <div className="background">
        <form id="form3">
          <h1>Enter your details for Login</h1>
          <br />
          <TextField
            id="outlined-required"
            required
            label="Email"
            className="input"
            placeholder="Enter your mail here"
            value={user.email}
            onChange={(event) =>
              setUser((previousState) => {
                return { ...previousState, email: event.target.value };
              })
            }
          />
          <br /> <br />
          <TextField
            id="outlined-password-input"
            label="Password"
            type="password"
            value={user.password}
            required
            className="input"
            placeholder="Enter your Password here"
            onChange={(event) =>
              setUser((previousState) => {
                return { ...previousState, password: event.target.value };
              })
            }
            autoComplete="current-password"
          />{" "}
          <br /> <br />
          <br />
          <Button onClick={handleClick} variant="outlined" className="btn1">
            Login
          </Button>
        </form>
      </div>
    </>
  );
}

export default withRouter(Login);
