import React, { useEffect, useState } from "react";
import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import { Button, CardActionArea, CardActions } from "@mui/material";
import { useSelector, useDispatch } from "react-redux";
import Box from "@mui/material/Box";
import Modal from "@mui/material/Modal";
// import { fetchSingleUserData } from "./apiServices";
import { singleData } from "./dataReducer";
import { deleteUserData } from "./apiServices";
import { serviceFailed } from "./dataReducer";
import { style } from "./boxStyle";

export default function ServiceUser(props) {
  const filterData = useSelector((state) => state.service.singleData);
  const failed = useSelector((state) => state.service.error);
  const loading = useSelector((state) => state.service.loading);

  const dispatch = useDispatch();
  const [open, setOpen] = useState(false);

  useEffect(() => {
    fetch("http://lcalhost:3001/users")
      .then((res) => res.json())
      .then((res) => {
        dispatch(singleData(res.users[res.users.length - 1]));
      })
      .catch(() => dispatch(serviceFailed()));

    // const res = fetchSingleUserData();
    // dispatch(singleData(res));
  }, [dispatch]);

  const handleClick = () => {
    deleteUserData(filterData.id);
    setOpen(true);
    setTimeout(() => {
      props.history.push("/");
    }, 2000);
  };

  return (
    <div id="background">
      {" "}
      <Card
        sx={{
          maxWidth: 445,
          minHeight: 350,
          paddingTop: 3,
          position: "relative",
          top: "20%",
          left: "38%",
          boxShadow: "gray 5px 9px  10px",
          borderRadius: "20px",
        }}
      >
        {loading && <h1>Loading...</h1>}
        {failed.length > 0 && !loading ? (
          <h1>{failed}</h1>
        ) : (
          <>
            <CardActionArea>
              <CardContent>
                <Typography gutterBottom variant="h4" component="div">
                  Name : {filterData.name} <br />
                </Typography>
                <Typography variant="h6" color="text.secondary">
                  {" "}
                  Email : {filterData.email} <br />
                  Mobile : {filterData.mob_no} <br />
                  Price : {filterData.price} Rs/-
                  <br />
                  Service : {filterData.role}
                  <br />
                  Address : {filterData.address}
                  <br />
                  Password : {filterData.password}
                  <br />
                  {/* Id : {filterData.id} */}
                </Typography>
              </CardContent>
            </CardActionArea>

            <CardActions>
              <Button
                color="primary"
                variant="outlined"
                onClick={() => props.history.push("/editDetails")}
              >
                Edit Details
              </Button>
              <Button
                variant="outlined"
                color="primary"
                onClick={() => props.history.push("/dashboard")}
              >
                Dashboard
              </Button>
              <Button variant="outlined" color="error" onClick={handleClick}>
                Delete
              </Button>
            </CardActions>
          </>
        )}
      </Card>
      <Modal
        open={open}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h5" component="h1">
            Data deleted succefully
          </Typography>
        </Box>
      </Modal>
    </div>
  );
}
