import React, { useEffect } from "react";
import "./App.css";
import { useState } from "react";
import Box from "@mui/material/Box";
import AppBar from "@mui/material/AppBar";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import Button from "@mui/material/Button";
import Toolbar from "@mui/material/Toolbar";

import Card from "@mui/material/Card";
import CardContent from "@mui/material/CardContent";
import Typography from "@mui/material/Typography";
import { CardActionArea, CardActions } from "@mui/material";
import { useSelector } from "react-redux";

import { fetchAllUserData, fetchFilterData } from "./apiServices";

export default function Dashboard() {
  const [service, setService] = useState("");
  const filterData = useSelector((state) => state.service.data);
  const failed = useSelector((state) => state.service.error);
  const loading = useSelector((state) => state.service.loading);
  const [isDisplay, setIsDisplay] = useState("none");

  const handleChange = (event) => {
    setService(event.target.value);
  };

  useEffect(() => {
    fetchAllUserData();
  }, []);

  const handleClick = () => {
    fetchFilterData(service);
  };

  const sort = () => {
    filterData.map((item) => {
      if (item.role === "") {
        item.filter();
        console.log(item);
      }
      return "";
    });
  };

  return (
    <div>
      {loading && <h1>Loading Data...</h1>}
      {failed !== "" && <h1>{failed}</h1>}
      {failed === "" && !loading ? (
        <>
          {sort()}
          <Box sx={{ flexGrow: 1 }}>
            <AppBar position="static">
              <Toolbar>
                <Typography variant="h6" component="div" sx={{ flexGrow: 1 }}>
                  Welcome
                </Typography>
                <Button style={{ display: isDisplay }} color="inherit">
                  Edit detail
                </Button>
              </Toolbar>
            </AppBar>
          </Box>
          <div id="form2">
            <Box sx={{ maxWidth: 320, marginLeft: 96 }}>
              <FormControl fullWidth>
                <InputLabel id="demo-simple-select-label">
                  Select Service
                </InputLabel>
                <Select
                  labelId="demo-simple-select-label"
                  id="demo-simple-select"
                  value={service}
                  label="Select Service"
                  onChange={handleChange}
                >
                  <MenuItem value={"plumber"}>Plumber</MenuItem>
                  <MenuItem value={"electrician"}>Electrician</MenuItem>
                  <MenuItem value={"washerman"}>Washerman</MenuItem>
                </Select>
              </FormControl>
            </Box>
            <Button onClick={handleClick} variant="outlined">
              Filter
            </Button>
          </div>

          <br />

          {filterData.length !== 0 &&
            filterData.map((item, index) => {
              return (
                <Card
                  key={index}
                  sx={{
                    float: "left",
                    p: 4,
                    m: 6,
                    bgcolor: "background.paper",
                    minWidth: 450,
                    borderRadius: 1,
                  }}
                >
                  <CardActionArea>
                    <CardContent>
                      <Typography gutterBottom variant="h4" component="div">
                        Name : {item.name}
                      </Typography>
                      <Typography variant="h6" color="text.secondary">
                        Price : {item.price} Rs/- <br />
                        {/* Email : {item.email} <br />
                    Mobile : {item.mob_no} <br /> */}
                        Service : {item.role}
                        <br />
                        Address : {item.address}
                      </Typography>
                    </CardContent>
                  </CardActionArea>
                  <CardActions>
                    <Button size="small" color="primary">
                      Contact
                    </Button>
                  </CardActions>
                </Card>
              );
            })}
        </>
      ) : null}
    </div>
  );
}
