import * as React from "react";
import { useState } from "react";
import "./App.css";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { withRouter } from "react-router";
import background from "./1.jpg";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  height: 200,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

function Signup(props) {
  const [name, setName] = useState("");
  const [data, setData] = useState([]);
  const [service, setService] = useState("");
  const [mobile, setMobile] = useState("");
  const [password, setPassword] = useState("");
  const [rate, setRate] = useState("");
  const [open, setOpen] = useState(false);

  const handleClick2 = () => {
    fetch("localhost:3001/users")
      .then((res) => res.json())
      .then((res) => console.log(res));
  };

  const handleClick = () => {
    let n, m, p, r;

    n = name;
    m = mobile;
    p = password;
    r = rate;
    let Plumber = new Array([]);
    let Electrician = new Array([]);
    let Carpenters = new Array([]);

    if (n !== "" && m !== "" && p !== "" && r !== "") {
      if (service === "Plumber") {
        Plumber = JSON.parse(localStorage.getItem("Plumber"))
          ? JSON.parse(localStorage.getItem("Plumber"))
          : [];

        Plumber.push({
          name: n,
          mobile: m,
          password: p,
          price: r,
        });
        localStorage.setItem("Plumber", JSON.stringify(Plumber));
      }

      if (service === "Electrician") {
        Electrician = JSON.parse(localStorage.getItem("Electrician"))
          ? JSON.parse(localStorage.getItem("Electrician"))
          : [];

        Electrician.push({
          name: n,
          mobile: m,
          password: p,
          price: r,
        });
        localStorage.setItem("Electrician", JSON.stringify(Electrician));
      }

      if (service === "Carpenters") {
        Carpenters = JSON.parse(localStorage.getItem("Carpenters"))
          ? JSON.parse(localStorage.getItem("Carpenters"))
          : [];

        Carpenters.push({
          name: n,
          mobile: m,
          password: p,
          price: r,
        });
        localStorage.setItem("Carpenters", JSON.stringify(Carpenters));
      }
    }
    if (n !== "" && m !== "" && p !== "" && r !== "" && service !== "") {
      setOpen(true);
      console.log(service.length);
      setTimeout(() => {
        props.history.push({
          pathname: "/userDetails",
          state: { service: service },
        });
      }, 2000);
    }
  };

  return (
    <div
      style={{
        backgroundImage: `url(${background})`,
        backgroundRepeat: "no-repeat",
        backgroundSize: "100% 100%",
        height: "100vh",
        width: "100%",
      }}
    >
      <form id="form" action="">
        <TextField
          id="outlined-required"
          required
          label="Name"
          placeholder="Enter your name here"
          value={name}
          onChange={(event) => setName(event.target.value)}
        />
        <br /> <br />
        <TextField
          id="outlined-number"
          required
          autoComplete="username"
          label="Mobile Number"
          type="number"
          value={mobile}
          onChange={(event) => setMobile(event.target.value)}
        />{" "}
        <br /> <br />
        <Box sx={{ maxWidth: 235, marginLeft: 101 }}>
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">Services</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={service}
              label="Services"
              onChange={(event) => setService(event.target.value)}
              required
              autoComplete="services"
            >
              <MenuItem value={"Plumber"}>Plumber</MenuItem>
              <MenuItem value={"Electrician"}>Electrician</MenuItem>
              <MenuItem value={"Carpenters"}>Carpenters</MenuItem>
            </Select>
          </FormControl>
        </Box>{" "}
        <br />
        <TextField
          id="outlined-number"
          autoComplete="rate"
          label="Price"
          type="number"
          required
          value={rate}
          onChange={(event) => setRate(event.target.value)}
        />{" "}
        <br /> <br />
        <TextField
          id="outlined-password-input"
          label="Password"
          type="password"
          required
          autoComplete="current-password"
          value={password}
          onChange={(event) => setPassword(event.target.value)}
        />{" "}
        <br /> <br />
        <Button
          onClick={handleClick}
          style={{ marginRight: "20px" }}
          variant="outlined"
        >
          Sign Up
        </Button>
        <Button
          onClick={() => props.history.push("/dashboard")}
          variant="outlined"
        >
          Dashboard
        </Button>
      </form>
      <Modal
        open={open}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h5" component="h1">
            Sign Up Successfully
          </Typography>
          <Typography
            id="modal-modal-title"
            variant="h6"
            sx={{ mt: 2 }}
            component="h2"
          >
            Name : {name}
          </Typography>
          <Typography id="modal-modal-description">
            Mobile Number : {mobile} <br /> Service : {service} <br /> Price :{" "}
            {rate} Rs/-
          </Typography>
        </Box>
      </Modal>
      <Button
        onClick={handleClick2}
        style={{ marginRight: "20px" }}
        variant="outlined"
      >
        data
      </Button>
    </div>
  );
}

export default withRouter(Signup);
