import * as React from "react";
import { useState, useEffect } from "react";
import "./App.css";
import InputLabel from "@mui/material/InputLabel";
import MenuItem from "@mui/material/MenuItem";
import Box from "@mui/material/Box";
import Typography from "@mui/material/Typography";
import Modal from "@mui/material/Modal";
import FormControl from "@mui/material/FormControl";
import Select from "@mui/material/Select";
import TextField from "@mui/material/TextField";
import Button from "@mui/material/Button";
import { withRouter } from "react-router";
import { singleData } from "./dataReducer";
import { useSelector, useDispatch } from "react-redux";

const style = {
  position: "absolute",
  top: "50%",
  left: "50%",
  transform: "translate(-50%, -50%)",
  width: 400,
  height: 200,
  bgcolor: "background.paper",
  border: "2px solid #000",
  boxShadow: 24,
  p: 4,
};

function EditUser(props) {
  const filterData = useSelector((state) => state.service.singleData);
  const dispatch = useDispatch();

  useEffect(() => {
    fetch("http://localhost:3001/users")
      .then((res) => res.json())
      .then((res) => {
        dispatch(singleData(res.users[res.users.length - 1]));
      });
  }, [dispatch]);
  const [first_name, setFirst_name] = useState(filterData.first_name);
  const [last_name, setLast_name] = useState(filterData.last_name);
  const [email, setEmail] = useState(filterData.email);
  const [role, setRole] = useState(filterData.role);
  const [gender, setGender] = useState(filterData.gender);
  const [address, setAddress] = useState(filterData.address);
  const [open, setOpen] = useState(false);
  const [rating, setRating] = useState(filterData.rating);
  const [mob_no, setMob_no] = useState(filterData.mob_no);
  const [price, setPrice] = useState(filterData.price);

  const handleClick = () => {
    let data = {
      first_name,
      last_name,
      email,
      gender,
      mob_no,
      role,
      address,
      price,
      rating,
    };
    fetch("http://localhost:3001/users", {
      method: "PUT",
      headers: {
        Accept: "application/json",
        "Content-Type": "application/json",
      },
      body: JSON.stringify(data),
    }).then((res) => res.json());

    if (
      first_name !== "" &&
      last_name !== "" &&
      email !== "" &&
      mob_no !== "" &&
      gender !== "" &&
      role !== "" &&
      rating !== "" &&
      price !== "" &&
      address !== ""
    ) {
      setOpen(true);
      setTimeout(() => {
        props.history.push("/userDetails");
      }, 2000);
    }
  };

  return (
    <div>
      <h3>Update your details</h3>
      <form id="form" action="">
        <TextField
          id="outlined-required"
          required
          label="First Name"
          placeholder="Enter your first name here"
          value={first_name}
          onChange={(event) => setFirst_name(event.target.value)}
        />{" "}
        <br /> <br />
        <TextField
          id="outlined-required"
          required
          label="Last Name"
          placeholder="Enter your last name here"
          value={last_name}
          onChange={(event) => setLast_name(event.target.value)}
        />{" "}
        <br /> <br />
        <TextField
          id="outlined-required"
          required
          label="Email"
          placeholder="Enter your mail here"
          value={email}
          onChange={(event) => setEmail(event.target.value)}
        />
        <br /> <br />
        <TextField
          id="outlined-number"
          required
          autoComplete="username"
          label="Mobile Number"
          type="number"
          value={mob_no}
          onChange={(event) => setMob_no(event.target.value)}
        />{" "}
        <br /> <br />
        <TextField
          id="outlined-required"
          required
          label="Gender"
          placeholder="Enter your gender here"
          value={gender}
          onChange={(event) => setGender(event.target.value)}
        />{" "}
        <br /> <br />
        <TextField
          id="outlined-required"
          required
          label="address"
          placeholder="Enter your address here"
          value={address}
          onChange={(event) => setAddress(event.target.value)}
        />
        <br /> <br />
        <Box sx={{ maxWidth: 235, marginLeft: 101 }}>
          <FormControl fullWidth>
            <InputLabel id="demo-simple-select-label">Service</InputLabel>
            <Select
              labelId="demo-simple-select-label"
              id="demo-simple-select"
              value={role}
              label="role"
              onChange={(event) => setRole(event.target.value)}
              required
              autoComplete="role"
            >
              <MenuItem value={"plumber"}>plumber</MenuItem>
              <MenuItem value={"washerman"}>washerman</MenuItem>
              <MenuItem value={"electrician"}>electrician</MenuItem>
            </Select>
          </FormControl>
        </Box>{" "}
        <br />
        <TextField
          id="outlined-number"
          autoComplete="price"
          label="Price"
          type="number"
          required
          value={price}
          onChange={(event) => setPrice(event.target.value)}
        />{" "}
        <br /> <br />
        <TextField
          id="outlined-number"
          autoComplete="rating"
          label="rating"
          type="number"
          required
          value={rating}
          onChange={(event) => setRating(event.target.value)}
        />
        <br /> <br />
        <Button
          onClick={handleClick}
          style={{ marginRight: "20px" }}
          variant="outlined"
        >
          Update
        </Button>
        <Button
          onClick={() => props.history.push("/userDetails")}
          style={{ marginRight: "20px" }}
          variant="outlined"
        >
          Cancel
        </Button>
      </form>
      <Modal
        open={open}
        aria-labelledby="modal-modal-title"
        aria-describedby="modal-modal-description"
      >
        <Box sx={style}>
          <Typography id="modal-modal-title" variant="h5" component="h1">
            Data Updated Successfully
          </Typography>
          <Typography
            id="modal-modal-title"
            variant="h6"
            sx={{ mt: 2 }}
            component="h2"
          >
            Name : {first_name} {last_name}
          </Typography>
          <Typography id="modal-modal-description">
            Mobile Number : {mob_no} <br /> role : {role} <br /> Price : {price}{" "}
            Rs/- <br />
            Email : {email} <br />
            Address : {address}
            <br />
            Rating : {rating}
          </Typography>
        </Box>
      </Modal>
    </div>
  );
}

export default withRouter(EditUser);
